import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './TodoList.css'; // Import the CSS file

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/'); // Redirect to login page if not authenticated
    }
  }, [navigate]);

  // Fetch todos from the backend
  useEffect(() => {
    const fetchTodos = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:3001/api/todo', {
          headers: { Authorization: token },
        });
        setTodos(response.data);
      } catch (err) {
        console.log(err);
      }
    };

    fetchTodos();
  }, []);

  // Add a new todo
  const handleAddTodo = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:3001/api/todo',
        { text: newTodo },
        { headers: { Authorization: token } }
      );
      setTodos([...todos, response.data]);
      setNewTodo('');
    } catch (err) {
      console.log(err);
    }
  };

  // Mark a todo as completed
  const handleCompleteTodo = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:3001/api/todo/${id}`,
        {},
        { headers: { Authorization: token } }
      );
      setTodos(todos.map((todo) => (todo._id === id ? response.data : todo)));
    } catch (err) {
      console.log(err);
    }
  };

  // Edit a todo
  const handleEditTodo = async (id, newText) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:3001/api/todo/${id}/edit`,
        { text: newText , done : false},
        { headers: { Authorization: token } }
      );
      setTodos(todos.map((todo) => (todo._id === id ? response.data : todo)));
    } catch (err) {
      console.log(err);
    }
  };

  // Delete a todo
  const handleDeleteTodo = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:3001/api/todo/${id}`, {
        headers: { Authorization: token },
      });
      setTodos(todos.filter((todo) => todo._id !== id));
    } catch (err) {
      console.log(err);
    }
  };

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/'); // Redirect to login page after logout
  };

  return (
    <div className="todo-container">
      <div className="header">
        <h1 className="todo-header">Todo List</h1>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </div>

      <form onSubmit={handleAddTodo} className="todo-form">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="Add a new todo"
          required
          className="todo-input"
        />
        <button type="submit" className="todo-button">Add</button>
      </form>

      <ul className="todo-list">
        {todos.map((todo) => (
          <li key={todo._id} className="todo-item">
            <span
              className={`todo-text ${todo.done ? 'completed' : ''}`}
              onClick={() => handleCompleteTodo(todo._id)}
            >
              {todo.text}
            </span>
            {/* Edit button */}
            <button onClick={() => {
              const newText = prompt('Edit Todo:', todo.text);
              if (newText) handleEditTodo(todo._id, newText);
            }}>
              Edit
            </button>
            {/* Delete button */}
            <button onClick={() => handleDeleteTodo(todo._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
